$(document).ready(function(){

    EventHoverFigure();
    HoverCard();
    readOnlyText();
    ScrollFade();    
});

function EventHoverFigure(){
	$("#animation-initial").hover(function(){
        $("#animation-initial").toggleClass('animated flip');
    }); 
}
function HoverCard(){
	$(".item-1").hover(function(){
		$(".item-1").toggleClass('animated pulse');
	});

	$(".item-2").hover(function(){
		$(".item-2").toggleClass('animated pulse');
	});

	$(".item-3").hover(function(){
		$(".item-3").toggleClass('animated pulse');
	});
}

function readOnlyText(){

var div = document.getElementById('title');
var text = 'Você é livre para enviar dinheiro a quem quiser.';

function write(str, el) {
  var char = str.split('').reverse();
  var typer = setInterval(function() {
    if (!char.length) return clearInterval(typer);
    var next = char.pop();
    el.innerHTML += next;
  }, 100);
}

write(text, div);

}


function ScrollFade(){

  $(window).scroll(function(){
      $("#cards").height(function(){
         $("#cards").addClass('animated zoomIn');
      });
  });

}

