$(document).ready(function(){
	ValueCotacao();
});

function ValueCotacao(){
	
	valueSend = $("#valueSend").val();
	valueGet = $("#valueGet").val();

	console.log('O valor enviado: ' + parseFloat(valueSend) + " e o valor recebido é " + parseFloat(valueGet));
	
	if ( valueSend > 0 ){

		$(":input").change(function(){
			valueCoin = 3.67;
			valueConvert = parseFloat(valueSend) * parseFloat(valueCoin);
			console.log('O valor da cotação é ' + parseFloat(valueConvert));
			valueGet = $("#valueGet").val(parseFloat(valueConvert)); 
		});
	}

}

